import {Component} from 'react';
import Qrcode from './Qrcode';

class Encode extends Component{

handleFormSubmit = (evt) => {
    evt.preventDefault();
}
render(){
    return(
    <div class="container">
        <div class="row">
            <div class="col-6">
                <Qrcode />
            </div>
            <div class="col-6">
                 <form onSubmit={this.handleFormSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input type="text" placeholder="Enter a title" value={this.state.title} onChange={this.handleTitleUpdate} className="form-control"/>
        </div>
        <div className="form-group">
          <label>
            Author
          </label>
          <input type="text" placeholder="Author's name" value={this.state.author} onChange={this.handleAuthorUpdate} className="form-control"/>
        </div>
        <div className="form-group">
          <label>Description</label>
          <textarea className="form-control" placeholder="Book Description" row="5" value={this.state.description} onChange={this.handleDescription}>
          {this.state.description}
          </textarea>
          </div>
        <div className="form-group d-flex justify-content-between">
          <button type="submit" className="btn btn-md btn-primary">{buttonText}</button>
          <button type="submit" className="btn btn-md btn-secondary" onClick={this.props.onCancelClick}>Cancel</button>
        </div>
      </form>
            </div>
        </div>
    </div>
    )
}
}
export default Encode;
