import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';


class EncodeForm extends Component {

    state = {
        content: this.props.content || '',
        logo: this.props.logo || '',
        useLogo: this.props.useLogo || 0,
    }
    handleContent = (evt) => {
        this.setState({content: evt.target.value})
    }
    getBase64 = (file) => {
        var document = "";
        let reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = function (){
            document = reader.result;
            console.log(reader.result);
        };
        reader.onerror = function (error){
            console.log('Error :', error);
        }
        console.log(document);
        return document;
    }
    handleLogo = (evt) => {
        let input = evt.target.files[0];
        if(!input) return;
        this.setState({logo: this.getBase64(input)})
    }
    handleUseLogo = (evt) => {
        console.log(evt.target.value);
        this.setState({useLogo: evt.target.value});
    }
    handleFormSubmit = (evt) => {
        evt.preventDefault();
        console.log(this.state.content)
        console.log(this.state.useLogo)
        console.log(this.state.logo)
//        this.props.onFormSubmit({...this.state});
    }
    render(){
        return(
                <form onSubmit={this.handleFormSubmit}>
                    <div className="form-group mb-3">
                        <label>Content</label>
                        <textarea className="form-control" placeholder="Enter data to encode" row="10" value={this.state.content} onChange={this.handleContent}>
                            {this.state.content}
                        </textarea>
                    </div>
                    <div className="form-group mb-3">
                        <label>Logo (40x40)</label>
                        <input type="file" value={this.state.logo} onChange={this.handleLogo} className="form-control"/>
                    </div>
                    <div className="form-check">
                        <input type="checkbox" className="form-check-input" id="useLogo" name="useLogo" onChange={this.handleUseLogo}/>
                        <label for="useLogo" className="form-check-label">Use logo</label>
                    </div>
                    <div className="form-group d-flex justify-content-between mt-4">
                        <button type="submit" className="btn btn-md btn-primary">Generate</button>
                        <button type="submit" className="btn btn-md btn-secondary" onClick={this.props.onCancelClick}>Cancel</button>
                    </div>
                </form>
        )
    }
}
export default EncodeForm;
