import {Component} from 'react';
import Qrcode from './Qrcode';

class Encode extends Component{

state = {
    title: this.props.title || '',
    content: this.props.content || ''
}
handleFormSubmit = (evt) => {
    evt.preventDefault();
}
render(){
    return(
    <div class="container d-flex justify-content-center my-4">
        <div class="row">
            <div class="col-6">
                <Qrcode />
            </div>
            <div class="col-6 mt-5">
                <div className="row justify-content-center">
                    <div className="col-9 mt-5 p-5" style={{boxShadow: '0 0 10px #ccc'}}>
                        <form onSubmit={this.handleFormSubmit}>
                            <div className="form-group mb-3">
                                <label>Content</label>
                                <textarea className="form-control" placeholder="Enter data to encode" row="8" value={this.state.description} onChange={this.handleDescription}>
                                    {this.state.description}
                                </textarea>
                            </div>
                            <div className="form-group mb-3">
                                <label>Logo</label>
                                <input type="file" placeholder="Enter a title" value={this.state.title} onChange={this.handleTitleUpdate} className="form-control"/>
                            </div>
                            <div className="form-check">
                                <input type="checkbox" className="form-check-input" id="use-logo" />
                                <label for="use-log" className="form-check-label">Use logo</label>
                            </div>
                            <div className="form-group d-flex justify-content-between mt-4">
                                <button type="submit" className="btn btn-md btn-primary">Generate</button>
                                <button type="submit" className="btn btn-md btn-secondary" onClick={this.props.onCancelClick}>Cancel</button>
                            </div>
                        </form>
                    </div>
                 </div>
            </div>
        </div>
    </div>
    )
}
}
export default Encode;
